
package com.axone.devintest.mod1Assignment1;

public class PrefixPostfix {

	
			
	public static void main(String[] args) {
		int a =10;
		int b=20;
		int c= 30;
		System.out.println("a = " + a + " b = " + b + " c = " + c);
		int pa = ++a;
		int pb = b++;
		System.out.println(pa+pb*c);
		System.out.println("a = " + a + " b = " + b + " c = " + c);
		
		// TODO Auto-generated method stub

	}

}
