package com.axone.devintest.mod1Assignment2;

/**
 * @author Aarthi Iyer
 *Program to print alternate element of String array
 * Excersise 8
 */
public class AlternateString {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] str = {"A","B","C","D","E","F","G"};
		int index=0;
	
		for (int i=index; i<str.length; i=i+2)
		{
			System.out.println(str[i]);
			
		
		}
		
		
			
		
	}

}
